function renderProducts() {
const productList = document.getElementById('product-list');
productList.innerHTML = '';
products.forEach((product, index) => {
  const item = document.createElement('div');
  item.className = 'product';
  item.innerHTML = `
    <img src="${product.img}" alt="${product.name}">
    <div class="product-content">
      <h3 class="product_name">${product.name}</h3>
      <p>${product.desc}</p>
      <p><strong>ÿßŸÑÿ≥ÿπÿ±:</strong> ${product.price} ÿØ.ÿ¨</p>
      <button onclick="addToCart(${index})">ÿ£ÿ∂ŸÅ ÿ•ŸÑŸâ ÿßŸÑÿ≥ŸÑÿ©</button>
      <button onclick="shareProduct(${index})" style="margin-top: 5px;">üì§ ŸÖÿ¥ÿßÿ±ŸÉÿ©</button>
    </div>`;
  productList.appendChild(item);
});
}

function addToCart(index) {
cart.push(products[index]);
renderCart();
totalprodects += 1;
max.textContent = totalprodects;
}

function shareProduct(index) {
const baseUrl = window.location.href;
const shareUrl = `${baseUrl}?product=${index}`; // ŸäŸÖŸÉŸÜŸÉ ÿ™ÿÆÿµŸäÿµ ÿßŸÑÿ±ÿßÿ®ÿ∑ ÿ≠ÿ≥ÿ® ÿµŸÅÿ≠ÿ© ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑŸÖŸÜÿ™ÿ¨

const product = products[index];

copyToClipboard(shareUrl);

}

function copyToClipboard(text) {
const tempInput = document.createElement("input");
tempInput.value = text;
document.body.appendChild(tempInput);
tempInput.select();
document.execCommand("copy");
document.body.removeChild(tempInput);
}
